    this.setState({ name, image, isLoading: false, hasImage});
    };
    

    updatePhoto = async (uri: String, message) => {
        try {
          const { user } = data;
          this.state.hasImage
            ? await user.image.set(uri)
            : await user.image.add(uri);
    
          this.props.toastManager.add(['', message], {
            appearance: 'success'
          });
        } catch (error) {
          this.props.toastManager.add(['Error', error.message], {
            appearance: 'error'
          });
        }
    };    

    render() {
        const { name, image, isLoading,driveTime,homeWeather,homeWeatherPenalty,workWeather,workWeatherPenalty,workGeo,homeGeo,transPref,test } = this.state;

        return (
        <SetupPageContent
            name={name}
            image={image}
            isLoading={isLoading}
            webId={this.props.webId}
            updatePhoto={this.updatePhoto}
            
            driveTime={driveTime}
            
            homeWeather={homeWeather}
            homeWeatherPenalty={homeWeatherPenalty}
            
            workWeather={workWeather}
            workWeatherPenalty={workWeatherPenalty}
            
            sliderVal={0}
            workGeo={workGeo}
            homeGeo = {homeGeo}
            transPref={transPref}
            test={test}
        />
        );
    }
}

export default withWebId(withToastManager(SetupComponent));
