import React from 'react';
// import { LogoutButton, Uploader } from '@inrupt/solid-react-components';
import isLoading from '@hocs/isLoading';
import {withTranslation } from 'react-i18next';
import {
  WelcomeWrapper,
  WelcomeCard
} from './welcome.style';
import { withToastManager } from 'react-toast-notifications';

/**
 * Welcome Page UI component, containing the styled components for the Welcome Page
 * Image component will get theimage context and resolve the value to render.
 * @param props
 */

function showVal(){
  alert(document.getElementById("prepTime").value)
}

const getResults = results => {
  if (results) {
    return results.map(r => (
      <p>
        {r.s},{r.p},{r.o}
      </p>
    ));
  } else {
    return "No results";
  }
};

const getDriveTime = driveTime => {
  if (driveTime) {
    return (
      <p>
        {driveTime} minutes
      </p>
    );
  } else {
    return "No results";
  }
};

const getWeather = weather => {
  if (weather) {
    return (
      <p>
        {weather}
      </p>

    );
  } else {
    return "No results";
  }
};

const getWeatherPenalty = weatherPenalty => {
  if (weatherPenalty) {
    return (
      <p>
        {weatherPenalty}
      </p>

    );
  } else {
    return "No results";
  }
};

const WelcomePageContent = props => {
  // const { webId, image, updatePhoto, toastManager, name, t } = props;
  const { name, t } = props;

  function calcTime(){
    var adjDriveTime = props.driveTime+props.weatherPenalty;
    if (document.getElementById("prepTime").value){
      adjDriveTime += parseInt(document.getElementById("prepTime").value);
    }
    alert(adjDriveTime);
    console.log(adjDriveTime);
  }

  return (
    <WelcomeWrapper data-testid="welcome-wrapper">
      <WelcomeCard className="card">
        <h3>
          {t('welcome.welcome')}, <span>{name}</span>
        </h3>


      </WelcomeCard>

      <WelcomeCard className="card">
        <div style={{width:'80%',marginLeft:'auto',marginRight:'auto'}}>How long do you need to get ready for work?</div>
        <input id="prepTime" placeholder="in minutes..." style={{width:'20%',marginLeft:'.5em', marginRight:'auto'}}></input>
        <button onClick={calcTime} styles={{width:'100'}}>Give me a call!</button>
      </WelcomeCard>

      <WelcomeCard className='card'>
        <div>Query Results:
            {// RG 2019-02-28
            // Render the query results
            getResults(props.results)}
        </div>
      </WelcomeCard>
      <WelcomeCard>
        <div>Travel Time:
          <Button onClick="getDriveTime(props.driveTime)"></Button>

        </div>
        </WelcomeCard>
      <WelcomeCard>
        <div>Weather info: </div>
        {getWeather(props.weather)}
        {getWeatherPenalty(props.weatherPenalty)} minutes
      </WelcomeCard>

    </WelcomeWrapper>
  );
};

export { WelcomePageContent };
export default withTranslation()(
  isLoading(withToastManager(WelcomePageContent))
);
