import React from 'react';
import isLoading from '@hocs/isLoading';
import {withTranslation } from 'react-i18next';
import {
  SetupWrapper,
  SetupCard
} from './setup.style';
import { withToastManager } from 'react-toast-notifications';
import StepSlider from '..\\..\\components\\Utils\\StepSlider\\StepSlider';

/**
 * Welcome Page UI component, containing the styled components for the Welcome Page
 * Image component will get theimage context and resolve the value to render.
 * @param props
 */

 
function showVal(){
    alert(document.getElementById("prepTime").value)
  }
  
  const getResults = results => {
    if (results) {
      return results.map(r => (
        <p>
          {r.s},{r.p},{r.o}
        </p>
      ));
    } else {
      return "No results";
    }
  };

  const getHomeAddress = homeAddr => {
    if (homeAddr) {
      return homeAddr.map(r => (
        <p>
          {r.streetAddress},{r.cityname},{r.statename}
        </p>
      ));
    } else {
      return "No results";
    }
  };

  
  const getDriveTime = driveTime => {
    if (driveTime) {
      // alert(driveTime);
      // console.log(driveTime);
      return (
        <p>
          {driveTime.toFixed(2)} minutes
        </p>
      );
    } else {
      return "No results";
    }
  };
  
  const logDriveTime = driveTime => {
    if (driveTime) {
      console.log(driveTime);
    }
  }
  
  const getWeather = weather => {
    if (weather) {
      return (
        <p>
          {weather}
        </p>
  
      );
    } else {
      return "No results";
    }
  };
  
  const getWeatherPenalty = weatherPenalty => {
    if (weatherPenalty) {
      return (
        <p>
          {weatherPenalty}
        </p>
  
      );
    } else {
      return "No results";
    }
  };
  
  const SetupPageContent = props => {
    const { name, t } = props;
    
  
    function calcTime(){
      var adjDriveTime = props.driveTime+props.weatherPenalty;
      if (document.getElementById("prepTime").value){
        adjDriveTime += parseInt(document.getElementById("prepTime").value);
      }
      // alert(adjDriveTime);
      console.log(adjDriveTime);
    }
  
    return (
      <SetupWrapper data-testid="welcome-wrapper">
        <SetupCard className="card">
          <h3>
            {t('welcome.welcome')}, <span>{name}</span>
          </h3>
          
          <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>How long do you need to get ready for work?</div>
        
        <StepSlider id="prepTime" onChange={ (e, val) => this.val = val } onDragStop={ (e) => this.setState({sliderVal:this.val})}/>
        
        {props.sliderVal}
  
        <div>
          <br/>
          <button onClick={calcTime} styles={{width:'100'}}>Show total time before work</button>
        </div>
          
        <div>
          <br/><br/>
          Query Results:
            {// RG 2019-02-28
            // Render the query results
            getResults(props.results)
            }
        </div>
  
        <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>
          <br/><br/>
          Travel Time:
          {getDriveTime(props.driveTime)}
          <button onClick={logDriveTime}>Get Travel Time</button>        
        </div>
  
        <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>
          <br/><br/>
          Weather info: 
          {getWeather(props.weather)}
          {getWeatherPenalty(props.weatherPenalty)} minutes
        </div>
  
        </SetupCard>
      </SetupWrapper>
    );
  };
  
  export { SetupPageContent };
  export default withTranslation()(
    isLoading(withToastManager(SetupPageContent))
  );
