import React from 'react';
import isLoading from '@hocs/isLoading';
import {withTranslation } from 'react-i18next';
import {
  SetupWrapper,
  SetupCard
} from './setup.style';
import { withToastManager } from 'react-toast-notifications';
import StepSlider from '..\\..\\components\\Utils\\StepSlider\\StepSlider';

/**
 * Welcome Page UI component, containing the styled components for the Welcome Page
 * Image component will get theimage context and resolve the value to render.
 * @param props
 */

  const getTransportationPreference = transPref => {

    if (transPref) {
      console.log("transPref")
      console.log(transPref)
      // return transPref.map(tP => (
      //   <p>
      //     Today's Transportation Method: {tP.mot}
      //   </p>
      // ));
      return (
        <p>
          {transPref}
        </p>
      )
    }
    else {
      return "No Results"
    }
  }

  const getWorkGeo = workGeo => {
    if (workGeo) {
        console.log("workGEO")
        console.log(workGeo)
        return workGeo.map(wG => (
          <p>
            Work Location: {wG.lat}, {wG.lon}
          </p>
        ));
    }
    else {
      return "No results";
    }
  };

  const getHomeGeo = homeGeo => {
    if (homeGeo) {
        console.log("homeGeo")
        console.log(homeGeo)
        return homeGeo.map(hG => (
          <p>
            Home Location: {hG.lat}, {hG.lon}
          </p>
        ));
    } 
    else {
      return "No results";
    }
  };

  const getDriveTime = driveTime => {
    if (driveTime) {
      // alert(driveTime);
      // console.log(driveTime);
      return (
        <p>
          {driveTime.toFixed(2)} minutes
        </p>
      );
    } else {
      return "No results";
    }
  };
  
  const logDriveTime = driveTime => {
    if (driveTime) {
      console.log(driveTime);
    }
  }
  
  const getWeather = (homeWeather,workWeather) => {
    console.log("getWeather: " + homeWeather + " | " + workWeather)
    if ((homeWeather) || (workWeather)) {
      return (
        <p>
          {workWeather}
          <br/>
          {homeWeather}
        </p>
  
      );
    } else {
      return "No results";
    }
  };
  
  const getWeatherPenalty = (workWeatherPenalty,homeWeatherPenalty) => {
    if ((homeWeatherPenalty) || (workWeatherPenalty)) {
      return (
        <p>
          {homeWeatherPenalty}
          <br/>
          {workWeatherPenalty}
        </p>
  
      );
    } else {
      return 0;
    }
  };
  
  const calcTime = () => {
    let prepTime = parseInt(document.getElementById("slider").innerText);
    console.log("prep: " + prepTime.toString())
    let homeWeatherTime = parseInt(document.getElementById("hwp").innerText);
    console.log("hwt: " + homeWeatherTime.toString())
    let workWeatherTime = parseInt(document.getElementById("wwp").innerText);
    console.log("wwt: " + workWeatherTime.toString())
    let driveTime = parseInt(document.getElementById("dt").innerText);
    console.log("dt: " + driveTime.toString())
    let totalTime = (prepTime + driveTime + homeWeatherTime + workWeatherTime).toFixed(2)
    console.log("tt: " + totalTime.toString())
    alert(totalTime)
  }


  const SetupPageContent = props => {
    const { name, t } = props;
     
    return (
      <SetupWrapper data-testid="welcome-wrapper">
        <SetupCard className="card">
          <h3>
            {t('welcome.welcome')}, <span>{name}</span>
          </h3>
          
          <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>How long do you need to get ready for work?</div>
        
        <StepSlider id="prepTime" />
          
        <div>
          <br/>
          <button onClick={calcTime} styles={{width:'100'}}>Show total time before work</button>
        </div>

        <div id="hwp" style={{display:"none"}}>
          {props.homeWeatherPenalty}
        </div>
        <div id="wwp"  style={{display:"none"}}>
          {props.workWeatherPenalty}
        </div>
        <div id="dt"  style={{display:"none"}}>
          {props.driveTime}
        </div>

        <div id="workTime" style={{paddingTop:"3em",width:"90%"}}>
          <input placeholder="When do you start work? <24hr format ex: 1430"/>
        </div>

        <br/><br/>
          
        <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>
          Query Results:
            {
              getWorkGeo(props.workGeo)
            }
            {
              getHomeGeo(props.homeGeo)
            }
        </div>
  
        <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>
          <br/><br/>
          Travel Time:
          {getDriveTime(props.driveTime)}
        </div>
  
        <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>
          <br/><br/>
          Weather info: 
          {getWeather(props.homeWeather,props.workWeather)}
          {getWeatherPenalty(props.homeWeatherPenalty,props.workWeatherPenalty)} minutes
        </div>
  
        <div style={{textAlign:'center',width:'80%',marginLeft:'auto',marginRight:'auto'}}>
          <br/><br/>
          Transportation info: 
          {getTransportationPreference(props.transPref)}
        </div>

        </SetupCard>
      </SetupWrapper>
    );
  };
  
  export { SetupPageContent };
  export default withTranslation()(
    isLoading(withToastManager(SetupPageContent))
  );
