import React, { Component } from 'react';
import WelcomePageContent from './welcome.component';
import { withWebId } from '@inrupt/solid-react-components';
import data from '@solid/query-ldflex';
import { withToastManager } from 'react-toast-notifications';

import $rdf from "rdflib";

const defaultProfilePhoto = '/img/icon/empty-profile.svg';

// RG - 2019-02-28
// The store is a local rdf store to hold data retrieved from Solid
const store = $rdf.graph();

// RG - 2019-02-28
// Loads the data from a URL into the local store
const loadFromUrl = (url, store) => {
  return new Promise((resolve, reject) => {
    let fetcher = new $rdf.Fetcher(store);
    try {
      fetcher.load(url).then(response => {
        resolve(response.responseText);
        console.debug(response.responseText);
        // $rdf.parse(response.responseText, store, $rdf.sym(url).uri,"application/rdf");
      });
    } catch (err) {
      reject(err);
    }
  });
};

// RG - 2019-02-28
// Prepares a query by converting SPARQL into a Solid query
const prepare = (qryStr, store) => {
  return new Promise((resolve, reject) => {
    try {
      let query = $rdf.SPARQLToQuery(qryStr, false, store);
      resolve(query);
    } catch (err) {
      reject(err);
    }
  });
};

// RG - 2019-02-28
// Executes a query on the local store
const execute = (qry, store) => {
  return new Promise((resolve, reject) => {
    console.debug("here");
    const wanted = qry.vars;
    const resultAry = [];
    store.query(
      qry,
      results => {
        console.debug("here1");
        if (typeof results === "undefined") {
          reject("No results.");
        } else {
          let row = rowHandler(wanted, results);
          console.debug(row);
          if (row) resultAry.push(row);
        }
      },
      {},
      () => {
        resolve(resultAry);
      }
    );
  });
};

// RG - 2019-02-28
// Puts query results into an array according to the projection
const rowHandler = (wanted, results) => {
  const row = {};
  for (var r in results) {
    let found = false;
    let got = r.replace(/^\?/, "");
    if (wanted.length) {
      for (var w in wanted) {
        if (got === wanted[w].label) {
          found = true;
          continue;
        }
      }
      if (!found) continue;
    }
    row[got] = results[r].value;
  }
  return row;
};


/**
 * Container component for the Welcome Page, containing example of how to fetch data from a POD
 */
class WelcomeComponent extends Component<Props> {
  constructor(props) {
    super(props);

    this.state = {
      name: '',
      image: defaultProfilePhoto,
      isLoading: false,
      hasImage: false,
      hits:[]
    };
  }
  componentDidMount() {
    if (this.props.webId) {
      this.getProfileData();
    }

    var homeAddress = '732 cleaver farm road, middletown de';
    var workAddress = '260 christiana rd, newcastle de';

    //MAPQUEST API
    const MAPQUEST_API = 'http://www.mapquestapi.com/directions/v2/route?key=DMw31TyjrjAee8VA1XAKnGGqw5sg7Nm3&from=';
    const TO = '&to=';

    fetch(MAPQUEST_API + homeAddress + TO + workAddress)
    .then(response =>  response.json())
    .then(resData => {

       let time = resData.route["time"]/60;
       this.setState({ driveTime: time }); //this is an asynchronous function
       console.log("fetch drivetime...");
       console.log({driveTime:time});
       console.log('end drivetime');
    })

    //WEATHER API
    const getLatLng = 'http://www.mapquestapi.com/geocoding/v1/address?key=DMw31TyjrjAee8VA1XAKnGGqw5sg7Nm3&location=260%20christiana%20rd%20newcastle%20de';

    fetch(getLatLng).then(response => response.json()).then(responseData => {
      console.log('Get latLng for weather...');
      this.setState({weatherLat:responseData['results']['0']['locations']['0']['latLng']['lat']});
      this.setState({weatherLng:responseData['results']['0']['locations']['0']['latLng']['lng']});
      console.log('latLng for weather:' + JSON.stringify(responseData['results']['0']['locations']['0']['latLng']));
      console.log('End get latLng for weather');
    }).then(() => {
      fetch(WEATHER_API+this.state.weatherLat+','+this.state.weatherLng+FORECAST).then(response =>  response.json()).then(weatherData => {
        this.setState({weather:weatherData['properties']['periods']['0']['shortForecast']})
        console.log("weather fetch for " + this.state.weatherLat.toString() + this.state.weatherLng.toString());
        console.log(this.state.weather);
        if ( (this.state.weather.includes('Rain')) || (this.state.weather.includes('Snow'))  ){
          console.log('inclement weather');
          this.setState({weatherPenalty:15});
        } 
        else{
          this.setState({weatherPenalty:0});
          console.log('No weather penalty');
        }
        console.log("end weather fetch");
      })});


    const WEATHER_API = 'https://api.weather.gov/points/';
    const FORECAST = '/forecast';

    // fetch(WEATHER_API+workAddress+FORECAST).then(response =>  response.json()).then(weatherData => this.setState({weather:weatherData}));   

  }//componentDidMount()


  componentDidUpdate(prevProps, prevState) {
    if (this.props.webId && this.props.webId !== prevProps.webId) {
      this.getProfileData();
    }
  }

  /**
   * This function retrieves a user's card data and tries to grab both the user's name and profile photo if they exist.
   *
   * This is an example of how to use the LDFlex library to fetch different linked data fields.
   */
  getProfileData = async () => {
    this.setState({ isLoading: true });
    let hasImage;

    /*
     * This is an example of how to use LDFlex. Here, we're loading the webID link into a user variable. This user object
     * will contain all of the data stored in the webID link, such as profile information. Then, we're grabbing the user.name property
     * from the returned user object.
     */
    const user = data[this.props.webId];
    const nameLd = await user.name;

    const name = nameLd ? nameLd.value : '';

    let imageLd = await user.image;
    imageLd = imageLd ? imageLd : await user.vcard_hasPhoto;

    let image;
